package bigframe.datagen.relational.tpcds;

import java.util.ArrayList;
import java.util.Date;
import java.util.List;

/**
 * Class for recording some basic information of the promotion generated by
 * tpcds.
 * 
 * @author andy
 * 
 */
public class TpcdsPromotedProduct {
	private Date dateBegin;
	private Date dateEnd;
	private List<Integer> products;

	public TpcdsPromotedProduct() {
		products = new ArrayList<Integer>();
	}

	public Date getDateBengin() {
		return dateBegin;
	}

	public Date getDateEnd() {
		return dateEnd;
	}

	public void addProduct(int prod) {
		products.add(prod);
	}

	public List<Integer> getProducts() {
		return products;
	}

	public void setProducts(List<Integer> prods) {
		products = prods;
	}
}
